import React, { useState } from 'react';
import axios from 'axios'; // Muista asentaa axios: npm install axios

// Login-komponentti käsittelee käyttäjän kirjautumisen
function Login() {
    // useState hook luo tilan käyttäjän kirjautumistiedoille: käyttäjänimi ja salasana
    const [credentials, setCredentials] = useState({ username: '', password: '' });

    // useState hookit onnistumis- ja virheviesteille
    const [success, setSuccess] = useState('');
    const [error, setError] = useState('');

    // handleChange-funktio päivittää tilan, kun käyttäjä muuttaa lomakkeen kenttää
    const handleChange = (e) => {
        // Päivitetään vastaava kenttä tilassa
        setCredentials({
            ...credentials,
            [e.target.name]: e.target.value
        });
    };

    // handleSubmit-funktio käsittelee lomakkeen lähetyksen
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Tyhjennetään viestit
        setError('');
        setSuccess('');

        try {
            // Lähetetään kirjautumistiedot backendille
            const response = await axios.post('http://localhost:5000/api/users', credentials);
            
            // Jos kirjautuminen onnistuu, näytetään ilmoitus
            setSuccess('Kirjautuminen onnistui!');
            setCredentials({ username: '', password: '' }); // Tyhjennetään lomake
        } catch (err) {
            // Jos kirjautumisessa tapahtuu virhe, näytetään virheilmoitus
            setError('Virhe kirjautumisessa: ' + (err.response?.data || 'Yhteys epäonnistui'));
        }
    };

    // Lomakkeen renderöinti
    return (
        <div>
            <h2>Kirjaudu sisään</h2>

            {/* Näytetään onnistumisviesti */}
            {success && <p style={{ color: 'green' }}>{success}</p>}

            {/* Näytetään virheviesti */}
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <form onSubmit={handleSubmit}>
                <label>
                    Käyttäjänimi:
                    <input
                        type="text"
                        name="username"
                        value={credentials.username}
                        onChange={handleChange}
                        required
                    />
                </label>
                <br />
                <label>
                    Salasana:
                    <input
                        type="password"
                        name="password"
                        value={credentials.password}
                        onChange={handleChange}
                        required
                    />
                </label>
                <br />
                <button type="submit">Kirjaudu</button>
            </form>
        </div>
    );
}

export default Login;
