// src/components/Register.js
import React, { useState } from 'react';

// Register-komponentti käsittelee käyttäjän rekisteröinnin
function Register() {
    // useState hook luo tilan käyttäjän tiedoille: käyttäjänimi, salasana ja bio
    const [user, setUser] = useState({ username: '', password: '', bio: '' });
    //tähän lisättävä hookit onnistumis viestille (success) ja error (error) viestille
    const [success, setSuccess] = useState('');
    const [error, setError] = useState('');

    // handleChange-funktio päivittää tilan, kun käyttäjä muuttaa lomakkeen kenttää
    const handleChange = (e) => {
        // Päivitetään vastaava kenttä tilassa
        setUser({
            ...user,
            [e.target.name]: e.target.value
        });
    };
    // handleSubmit-funktio käsittelee lomakkeen lähetyksen

    const handleSubmit = (e) => {
        e.preventDefault();
        //tähän kohti tuotava uutta materiaalia liittyen onnistumis ja error viesteihin
        if (user.username && user.password) {
            // Jos rekisteröinti onnistuu, näytetään onnistumisviesti
            console.log('Käyttäjä rekisteröitynyt:', user);
            setSuccess('Käyttäjä rekisteröityi onnistuneesti');
            setError(''); // Nollataan virheviesti

        
            // Jos rekisteröinti epäonnistuu, näytetään virheviesti
            setError('Kaikki kentät ovat pakollisia');
            setSuccess(''); // Nollataan onnistumisviesti




        // Tulostetaan rekisteröityneen käyttäjän tiedot konsoliin
        console.log('Käyttäjä rekisteröitynyt:', user);
        // Näytetään ilmoitus onnistuneesta rekisteröinnistä
        alert('Käyttäjä rekisteröityi onnistuneesti');

        //tähän laitettava vielä rekisteröintiin liittyvää tietoa
    };
    // Lomakkeen renderöinti
    return (
        <div>
            <h2>Rekisteröinti</h2>
             {success && <p style={{ color: 'green' }}>{success}</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}
            
            <form onSubmit={handleSubmit}>
                <label>
                Käyttäjänimi:
                    {/* Tekstikenttä, joka päivittää username-tilan */}
                    <input
                        type="text"
                        name="username"
                        value={user.username}
                        onChange={handleChange}
                        required
                    />
                </label>
                <br />
                <label>
                    Salasana:
                    {/* Salasanojen syöttökenttä, joka päivittää password-tilan */}
                    <input
                                            type="password"
                                            name="password"
                                            value={user.password}
                                            onChange={handleChange}
                                            required
                                        />
                                    </label>
                                    <br />
                                    <label>
                                        Bio:
                                        {/* Tekstialue, joka päivittää bio-tilan */}
                                        <textarea
                                            name="bio"
                                            value={user.bio}
                                            onChange={handleChange}
                                        />
                                    </label>
                                    <br />
                                    {/* Rekisteröinti-painike */}
                                    <button type="submit">Rekisteröidy</button>
                                </form>
                            </div>
                        );
                    }}
                    export default Register;
